void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 m = iMouse.xy/iResolution.xy;
  vec2 src = m;
  vec2 uv = fragCoord/iResolution.xy;
  vec2 ray = uv;
  vec2 dir = normalize(-(ray-src));

  vec2 sph = vec2(0.5,0.5);
  float r = 0.01;
  float sqr_r = pow(r,2.0);

  for(int i = 0; i < 200; i++) {
    float dst = abs(pow(ray.x-sph.x,2.0)+pow(ray.y-sph.y,2.0)-sqr_r);
    ray.x += dir.x*dst;
    ray.y += dir.y*dst;
    if (dst < 1.0) {
        fragColor = vec4(abs(ray.xxxx*0.1)+abs(ray.yyyy*0.1));
    }
  }
}
