float circle(vec2 uv, vec2 offset,float r, float edgeWidth)
{
    return smoothstep(r,r-edgeWidth,length(uv-offset));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
	uv.xy -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    float d = circle(uv, vec2(0,0), 0.4, 0.05); 
	d -= circle(uv, vec2(-.1,.1), 0.05, 0.01);	
	d -= circle(uv, vec2(.1,.1), 0.05, 0.01);	

     vec3 col = vec3(2.0,3.0,0)*d;

    fragColor = vec4(col,1.5);
}
