/* v1.0 */
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(uv, 0.);

    float p = sin(iTime)*.5+.5;

    vec2 pOrigin = vec2(0.,0.); //Change origin here
    vec2 pDir = vec2(1.,0.); //Change direction here

    vec2 uvPOrigin = pOrigin+pDir*p;

    float signDir1 = dot(uv-uvPOrigin, pDir);

    if(signDir1 > 0.) 
        col = vec3(0.);


    fragColor = vec4(col,1.0);
}
