//just messing around

float idist(float a, float b, float c, float d){
   return 1./sqrt((a-c)*(a-c)+(b-d)*(b-d))*40.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0,0,0);
    col.r+=idist(fragCoord.x,fragCoord.y,iMouse.x,iMouse.y);
    col.r+=idist(fragCoord.x,fragCoord.y,200.0,200.0+sin(iTime)*100.);

    col.r = smoothstep(col.r,0.,1.);//makes them 'interesting'

    fragColor = vec4(col,1.0);
}
