float minValue(float bg, vec2 uv)
{
    vec4 fg = texture(iChannel1, uv);
    float fvalue = dot(fg, vec4(0.3, 0.6, 0.1, 0.0));
    float fac = distance(fg.rgb, vec3(13.0 / 255.0, 163.0 / 255.0, 37.0 / 255.0)) / 1.738;
    fac = max(0.0, fac - 0.07) / 0.9 * 20.0;
    fac = clamp(pow(fac, 0.3), 0.0, 1.0);
    return mix(bg, fvalue, fac);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    float value = dot(texture(iChannel0, uv), vec4(0.3, 0.6, 0.1, 0.0));

    float x = fragCoord.x;
    float y = fragCoord.y;

    float noise = mod(52.9829189 * fract(0.06711056 * x + 0.00583715f * y + cos(iTime)), 1.0);

    float b = noise <= minValue(value, uv) ? 1.0 : 0.0;

    fragColor = vec4(b);
}
