#define Pi 3.14159
#define QPi Pi/4.
#define HPi Pi/2.

float rand(vec2 c, float tt){
    //float t = 1.-cos(tt);
	float r = fract(sin(dot(c.xy,vec2(12.9898,78.233))) * 43758.5453);
    if (r > 0.) r = fract(r+tt);
    return floor(r*255.)/255.;
}

vec3 rand3d(vec2 uv, float xt) { 
    return vec3(rand(uv.yx, xt),rand(1.-uv.yy, xt),rand(uv.xy, xt));
}

vec3 colorize(vec3 color, float c, float t) {
    if (color.x > max(color.y, color.z)) {
        color.x *= c;
        color.y = color.z = min(color.y, color.z)*t; 
    }

    if (color.y > max(color.x,color.z)) {
        color.y *= c;
        color.z = color.x = min(color.x, color.z)*t; 
    }

    if (color.z > max(color.y, color.z)) {
        color.z *= c;
        color.y = color.x = min(color.y, color.x)*t; 
    }
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uvr = fragCoord.xy / iResolution.xy;
    uvr = (uv - 0.5) * .025*(rand(vec2(iTime),Pi+iTime)*100.)/5. + 0.5;
    vec2 uv2 = fragCoord.xy / iResolution.xy;
    vec2 uv3 = fragCoord.xy / iResolution.xy;
    vec2 st = (uv - 0.5) * 1.25 + 0.5;


    float mid = texture( iChannel0, vec2(.125,0.25) ).x;
    float wave = texture( iChannel0, vec2(st.x,0.75) ).x;

    float fft  = texture( iChannel0, vec2(fract(st.x*10.),0.25) ).x; 
    float xt = fft * pow(mid,10.)*200.;

        uvr = floor(uvr * 10.)/10.;
    vec2 a = vec2(.5)+vec2(fract(rand(uvr.xx*cos(iTime), xt)),
        fract(rand(1.-uvr.xx*sin(iTime), xt/2.*cos(iTime))));
    vec2 b = vec2(.5)-vec2(fract(rand(uvr.xx*tan(iTime), xt))/2.,
        fract(rand(1.-uvr.xx*atan(iTime),xt/2.))/2.);
    vec2 cr = step(uvr, a)-step(uvr, b);
    float rr = cr.x*cr.y;


    if ((xt*rr+sin(iTime))/2. > 0.5) {
        uv.x = 1.-rr;
    }


    float size = 20.*uv.x;
    float light = 1.; //5
    uv = floor(uv*size)/size;

    vec3 color = rand3d(uv, xt)/light;

    color = colorize(color, .001, .001);

    vec2 ce = step(uv2, vec2(.7, .75))-step(uv2, vec2(.3, .25));
    float cc = ce.x*ce.y;
    uv2 = (uv2 - 0.5) * 2. + 0.5;
    float size2 = 20.*uv2.x;
    uv2 = floor(uv2*size2)/size2;
    vec3 color2 = cc*rand3d(uv2, xt/2.)/light;
    color2 = colorize(color2, .001, .001);

    vec2 ce2 = step(uv3, vec2(.55, .65))-step(uv3, vec2(.15, .15));
    float cc2 = ce2.x*ce2.y;
    uv3 = (uv3 - 0.5) * 2. + 0.5;
    float size3 = 20.*uv3.x;
    uv3 = floor(uv3*size3)/size3;
    vec3 color3 = cc2*rand3d(uv3, xt/3.)/light;
    color3 = colorize(color3, .001, .001);

    color = max(color-cc, color2);
    color = max(color-cc2, color3);

    if (mid > .6 && color.b > 0.) {
        color = colorize(color, 2000., .0001);
    }



    if (rr/2. > 0.55 && color.b > 0.) color = colorize(color, 2000., .0001);;
    //color = ;


	fragColor = vec4(color,1.0);
}
