void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = fragCoord/iResolution.xy;
    float mv1 = (sin(iTime-uv.x*3.0)+3.2)/10.0;
    float mv2 = (sin(iTime-uv.x*3.0)+6.5)/10.0;
    float wv1 = (sin(iTime-uv.x*3.0))/3.0;
    float wv2 = (sin(iTime-(uv.x-0.32)*10.0))/3.0;

    if(mv1 > uv.y){
                fragColor = vec4(0,0.1,2,1)*(wv1+0.5)/0.1;
        } else if(mv2 > uv.y){
                fragColor = vec4(1,1,1,1)*(wv1+0.6)/0.65;
        } else if(mv2 < uv.y){
                fragColor = vec4(1,0,0.2,1)*(wv1+0.6)/0.65;
        }
}
