float tunnel(vec2 uv, float aspect, vec2 mod)
{
    // base vectors
    vec2 up = vec2(0,1);
    vec2 right = vec2(1,0);
    vec2 mid = vec2(0.5 * aspect, 0.5);
    vec2 toFrag = uv - mid;

    // angle + log distance from middle
    float angle = acos(dot(toFrag, up) / (length(toFrag)));
    float dist = log(length(toFrag));

    // have two sides to it
    if (dot(toFrag, right) > 0.0)
    {
        angle = -angle;
    }

    // animate tunnel 
    dist += cos(iTime / 3.0) * cos(dist * 3.0 + angle * 2.0) / 4.5;

    // resolve texture UV
    vec2 texUv = vec2(angle / 3.1415192, 1.0-dist);

    // tiling
    texUv.x *= 8.0;
    texUv.y *= 5.0;

    //animate
    texUv.x += iTime / 2.0;
    texUv.y += iTime / 0.5 * mod.x;

    // animate texture pickup
    texUv.y += 1.75 * cos(dist * sin(iTime / 3.0)) * mod.y;

    return texture(iChannel0, texUv).r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // find aspect ratio
    float aspect = iResolution.x / iResolution.y;
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= aspect;

    // animate the window
    uv.x += sin(iTime) / 3.6;
    uv.y += cos(iTime * 1.23) / 4.2;

    // get slightly different tunnels
    float t1 = tunnel(uv, aspect, vec2(1.0, 1.0));
    float t2 = tunnel(uv, aspect, vec2(1.01, 1.1));
    float t3 = tunnel(uv, aspect, vec2(1.02, 1.2));

    // compose image
    vec3 col = vec3(smoothstep(t1, t2, 0.5), smoothstep(t1,t3, 0.5), smoothstep(t2,t3, 0.5));

   fragColor = vec4(col,1.0);
}
