#define pi 3.1415926

mat3 yaw_pitch_roll(float yaw, float pitch, float roll)
{
    mat3 R = mat3(vec3(cos(yaw), sin(yaw), 0.0), vec3(-sin(yaw), cos(yaw), 0.0), vec3(0.0, 0.0, 1.0));
    mat3 S = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(pitch), sin(pitch)), vec3(0.0, -sin(pitch), cos(pitch)));
    mat3 T = mat3(vec3(cos(roll), 0.0, sin(roll)), vec3(0.0, 1.0, 0.0), vec3(-sin(roll), 0.0, cos(roll)));

    return R * S * T;
}

#define DIST_MAX 1000.0

#define MAX_RAY_STEPS 64

float torus(vec3 p, vec2 t)
{
    return length(vec2(length(p.xy) - t.x, p.z)) - t.y;
}

bool example()
{
    return false;// fract(iTime / 10.0) < 0.5;
}

// https://www.shadertoy.com/view/DtjyWD integer division - rounding down
ivec3 div_floor(ivec3 a, int b) // vector version thanks to Fabrice
{
    ivec3  S = (sign(abs(a * b))-sign(a * b)) / 2; // 0 if a*b >= 0
    return S * ((1 - abs(a)) / abs(b) - 1) + (1 - S) * (a / b); // emulates ()?:
}

ivec3 hexagonal(ivec3 i, int w) // group triangular coordinates to hexagonal coordinates (jt)
{
    return div_floor(i.zxy-i.yzx-(w & 1),3);
}

vec3 flatten(vec3 v)
{
    vec3 n = vec3(1,1,1);
    return v - dot(v,n)*n/dot(n,n); // remove component along diagonal
}

vec4 triangle_prism_trace(vec3 ray_pos_orig, vec3 ray_dir_orig) // step triangles by stepping through voxels along plane orthogonal to main diagonal (additional fourth dimension to step along prism height) by jt
{
    // coordinate-system along plane orthogonal to diagonal (1,1,1)
    mat3 T =
        mat3
        (
            vec3(+1.0/sqrt(2.0),-1.0/sqrt(2.0), 0.0),
            vec3(-1.0/sqrt(6.0),-1.0/sqrt(6.0), 2.0/sqrt(6.0)),
            vec3(-1.0/sqrt(3.0),-1.0/sqrt(3.0),-1.0/sqrt(3.0))
        );
/*
    mat2x3 T =
        mat2x3
        (
            vec3(+1.0/sqrt(2.0),-1.0/sqrt(2.0), 0.0),
            vec3(-1.0/sqrt(6.0),-1.0/sqrt(6.0), 2.0/sqrt(6.0))
        );
*/
    // step through voxels along plane orthogonal to main diagonal (1,1,1)
    vec4 ray_pos = vec4(T * vec3(ray_pos_orig.xy, 0), ray_pos_orig.z);
    vec4 ray_dir = vec4(T * vec3(ray_dir_orig.xy, 0), ray_dir_orig.z);
//    vec4 ray_pos = vec4(T * ray_pos_orig.xy, ray_pos_orig.z);
//    vec4 ray_dir = vec4(T * ray_dir_orig.xy, ray_dir_orig.z);

    // initialization from https://www.shadertoy.com/view/4dX3zl Branchless Voxel Raycasting by fb39ca4 implementing https://lodev.org/cgtutor/raycasting.html
    ivec4 map_pos = ivec4(floor(ray_pos));
    vec4 delta_dist = 1.0 / abs(ray_dir);
    ivec4 ray_step = ivec4(sign(ray_dir));
    vec4 side_dist = (sign(ray_dir) * (vec4(map_pos) - ray_pos) + (sign(ray_dir) * 0.5) + 0.5) * delta_dist;

    for (int i = 0; i < MAX_RAY_STEPS; i++)
    {
        // core based on https://www.shadertoy.com/view/4dX3zl Branchless Voxel Raycasting by fb39ca4 implementing https://lodev.org/cgtutor/raycasting.html
        bvec4 mask = lessThanEqual(side_dist.xyzw, min(min(side_dist.yzwx, side_dist.zwxy), side_dist.wxyz));
        side_dist += vec4(mask) * delta_dist;
        map_pos += ivec4(vec4(mask)) * ray_step;

        vec3 unpos = vec3((2.0*vec3(hexagonal(map_pos.xyz, map_pos.w))*T).xy, map_pos.w); // NOTE: after transforming back z component should be 0 here so we can drop it
//        vec3 unpos = vec3((2.0*vec3(hexagonal(map_pos.xyz, map_pos.w))*T), map_pos.w);
        float d = length(vec4(mask) * (side_dist - delta_dist)); // assume ray_dir normalized
        bool solid = false;
        if(example())
        {
            //solid = (torus(ray_pos_orig+ray_dir_orig*d, vec2(9.0, 3.0)) < 0.0);
            //ivec3 unmap = ivec3(2*map_pos.x-map_pos.z, 2*map_pos.y-map_pos.z, 2*map_pos.w); // works somehow but asymmetric
            //solid = torus(vec3(unmap), vec2(19.0, 7.0)) < 0.0;
            solid = torus(unpos, vec2(9.0, 3.5)) < 0.0;
        }
        else
            //solid = length(vec4(map_pos)) < 7.0; // 4d sphere (before converting cell position back to 3d)
            solid = length(unpos) < 6.9; // 3d sphere XXX why do we have to subtract 0.1 here?!
            //solid = length(unpos.xy) < 15.0 && (sin(unpos.x/2.0)*sin(unpos.y/2.0)) > abs(unpos.z / 4.5); // XXX more complex shape reveal shadow bugs near light XXX

        if(solid)
        {
            // NOTE: currently no distinction between front and back (restored by caller)
            if(mask.w) return vec4(vec3(0,0,1),d);
            // Calculate the normal-vector directly from the mask (requires square matrix variant)
            return vec4(inverse(T)*normalize(flatten((vec3(mask)*vec3(-ray_step)))), d);
            if(mask.z) return vec4(vec3(0,1,0),d);
            if(mask.y) return vec4(vec3(-sqrt(3.0)/2.0,-0.5,0),d);
            if(mask.x) return vec4(vec3(+sqrt(3.0)/2.0,-0.5,0),d);
            //if(mask.w) return vec4(vec3(0,0,1),d);
            //if(mask.z) return vec4(vec3(-sin((0.0/3.0)*2.0*pi),cos((0.0/3.0)*2.0*pi),0),d);
            //if(mask.y) return vec4(vec3(-sin((1.0/3.0)*2.0*pi),cos((1.0/3.0)*2.0*pi),0),d);
            //if(mask.x) return vec4(vec3(-sin((2.0/3.0)*2.0*pi),cos((2.0/3.0)*2.0*pi),0),d);
            return vec4(0,0,0,d); // just in case...
        }
    }

    return vec4(-ray_dir_orig, DIST_MAX); // sky-sphere
}

void mainImage(out vec4 fragColor, in vec2 I)
{
    bool demo = all(lessThan(iMouse.xy, vec2(10.0)));
    vec2 R = iResolution.xy;
    //I = 2.0 * I.xy / R - 1.0;
    //I.x *= R.x / R.y;
    I = (2.0 * I - R) / R.y; // concise pixel-position mapping thanks to Fabrice
    float yaw = 2.0 * pi * float(iMouse.x) / float(R.x);
    //if(iMouse.y < iResolution.y / 2.0)
    //    yaw = 1.0*pi-yaw;
    //float pitch = pi - pi / 2.0 * float(iMouse.y) / float(R.y);
    float pitch = -pi/4.0 - 1.5 * pi * float(iMouse.y) / float(R.y);
    yaw = !demo ? yaw : 2.0 * pi * fract(iTime * 0.01);
    pitch = !demo ? pitch : 1.25 * pi/2.0;

    vec3 ray_pos = vec3(0.0, 0.0, -25.0);
    vec3 ray_dir = vec3(I.x, I.y, 2.0); // NOTE: un-normalized direction appears to work fine here!
    //ray_dir = normalize(ray_dir); // looks like ray-direction does not need to be normalized

    mat3 M = yaw_pitch_roll(yaw, pitch, 0.0);
    ray_pos = M * ray_pos;
    ray_dir = M * ray_dir;
    ray_pos.z += 0.5;

    vec3 color = vec3(0);
    vec4 result = triangle_prism_trace(ray_pos, ray_dir);
    if(result.w < DIST_MAX)
    //if(result.w < 12.0 + 5.0 * cos(iTime))
    {
        color = vec3(1);
        if(example())
        {
            vec3 light_dir = normalize(vec3(5,2,1));
            float diffuse = 0.1 + max(0.0, dot(result.xyz, light_dir));
            color = 0.5 + 0.5 * result.xyz; // debug normals
            color *= diffuse;
        }
        else
        {
            float t = iTime * 2.0 * pi / 20.0;
            result.xyz *= dot(result.xyz, ray_dir) < 0.0 ? +1.0 : -1.0; // repair front-back
            vec3 dst = ray_pos + ray_dir * result.w;
            // multiple close-up point-lights to verify visually that normals are correct
            vec3 light_pos0 = 8.0*vec3(-cos(t),-sin(t),0.0);
            vec3 light_pos1 = 8.0*vec3(cos(t),0.0,sin(t));
            vec3 light_pos2 = 8.0*vec3(0.0,-cos(t),-sin(t));
            vec3 light_diff0 = light_pos0 - dst;
            vec3 light_diff1 = light_pos1 - dst;
            vec3 light_diff2 = light_pos2 - dst;
            float light_dist0 = length(light_diff0);
            float light_dist1 = length(light_diff1);
            float light_dist2 = length(light_diff2);
            float shadow0 = triangle_prism_trace(dst, light_pos0 - dst).w < light_dist0 ? 0.0 : 1.0;
            float shadow1 = triangle_prism_trace(dst, light_pos1 - dst).w < light_dist1 ? 0.0 : 1.0;
            float shadow2 = triangle_prism_trace(dst, light_pos2 - dst).w < light_dist2 ? 0.0 : 1.0;
            vec3 light_dir0 = light_diff0 / light_dist0;
            vec3 light_dir1 = light_diff1 / light_dist1;
            vec3 light_dir2 = light_diff2 / light_dist2;
            vec3 light_color = vec3(1);
            float brightness = 0.0;
            brightness += shadow0 * max(0.0, dot(result.xyz, light_dir0)) / (light_dist0 * light_dist0);
            brightness += shadow1 * max(0.0, dot(result.xyz, light_dir1)) / (light_dist1 * light_dist1);
            brightness += shadow2 * max(0.0, dot(result.xyz, light_dir2)) / (light_dist2 * light_dist2);
            vec3 diffuse = 10.0 * light_color * brightness;
            float ambient = 0.05;
            color = (diffuse + ambient) * (0.5 + 0.5 * result.xyz); // visualize normals
            //color = vec3(fract(result.w));

            //color = 0.5 + 0.5 * result.xyz; // debug normals
        }
    }
    else
    {
        color = vec3(0);
    }
    color = tanh(color); // roll-off / soft-clamp overly bright colors
    fragColor = vec4(sqrt(color), 1.0); // gamma
    //fragColor = vec4(color, 1.0); // no gamma
}
