const float frequency1 = 50.0;
const float frequency2 = 50.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/min(iResolution.x,iResolution.y);

    vec3 col1 = vec3(sin(frequency1*length(uv-0.5)-iTime)+1.0)/4.0;
    vec3 col2 = vec3(sin(frequency2*length(uv+vec2(-1.5,-0.5))-iTime)+1.0)/4.0;

    // Output to screen
    fragColor = vec4(col1+col2,1.0);
}
