float easeInQ(float x) {
    return pow(x, 5.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    uv *= 30.0;
    vec2 pid = floor(uv);
    uv = fract(uv);

    float s = step(max(abs(uv.x), abs(uv.y)), 1.0);

    vec2 m = mod(pid, 2.0);
    fragColor = vec4( vec3(m.x, 0.5 * m.y, 1.0 - 0.5 * m.x) * s * easeInQ(abs(sin(iTime * 1.33 + pid.x)) * abs(cos(iTime * 1.33 + pid.y))), 1.0);
}
