vec3 palette(float t){
    vec3 a = vec3(1., 0.2, 1.);
    vec3 b = vec3(0.2, 0.4, 0.2);
    vec3 c = vec3(2.0, 1.0, 1.0);
    vec3 d = vec3(0.00, 0.25, 0.25);
    return vec3(fract(t), fract(t), fract(t));
    //return vec3(fract((t*2.)), fract((t*3.)), fract((t+5.0)));
    return a + b*cos(2.*3.141592*(c*t+d));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec2 uv0 = uv;


    uv *= 1.5;
    uv = fract(uv);
    uv -= 0.5;

    float d = length(uv);
    vec3 col = palette(length(uv0)+iTime*0.1);

    //d = sin(d*8. + iTime);
    //d = abs(d);
    //d = 1. / d;
    //d = ((sin(iTime*0.5)+1.)*0.5*0.5+0.5)/d;
    //d = smoothstep(0.0, 1.0, d);
    d = sin(sin(d)*5000. + iTime)/8.;
    d = abs(d);
    d = 0.02 / d;

    col *= d;
    fragColor = vec4(col, 1.0);
}
