#define move(s) s * cos(t * (s + vec2(0, -1)) + vec2(-1, 10)) * vec2(2, 1)
#define draw(p, s, c)                                              \
            o = mix(o, vec4 c,                                     \
                        smoothstep(4./R.y, .0, length(p) - s)      \
                    );

void mainImage(out vec4 o, vec2 u){
    float t = iTime * .4;

    vec2 A, B, a, b,
         R = iResolution.xy,
         q = vec2(1., sqrt(3.));

    u = 12.*(u - R / 2.) / R.y;

    A = mod(u    , 2. * q) - q;
    B = mod(u + q, 2. * q) - q;
    o = vec4(1.-.5 * min(dot(A, A), dot(B, B)));

    a = move(5.),
    b = move(4.),

    draw(
        clamp(
            dot(u - a, b - a) / dot(b - a, b - a), 
            .0, 1.
        ) * (b - a) - u + a,
        .2, (.9, .8, 0, 0)
    );

    draw(u - a, .3, (1, 0, 0, 0));
    draw(u - b, .3, (0, 0, 1, 0));        
}
