// Plasma
#define PI 3.14159
float plas(vec2 uv, float s, float t)
{
    float v0 = sin(uv.x * s + t);
    float v1 = sin(s * (uv.x * sin(t*0.5) + uv.y * cos(t * 0.333333))+t); 
    vec2 c = vec2( uv.x + 0.5 *sin(t*0.2), uv.y + 0.5 * cos(t * 0.333333));
    float v2 = sin( sqrt(s * 33.0 * (c.x*c.x + c.y*c.y)+1.0+t) );
    return v0+v1+v2;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 uv = (fragCoord-0.5 * iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.0);
	float t = iTime * 0.3;
    float p = plas(uv, 10.0+sin(iTime), t);
	vec3 col0 = clamp(vec3(sin(p*PI), cos(p*PI), sin(t*(cos(p*PI*2.0)))),0.0,1.0);
	vec3 col1 = clamp(vec3(sin(p*PI), sin(p*PI+2.0*PI*0.33333) , sin(p*PI+4.0*PI*0.33333)),0.0,1.0);
    vec3 col2 = clamp(vec3(1.0, cos(p*PI), sin(p*PI)),0.0,1.0);
    vec3 colbw = vec3(sin(p*5.0*PI));

    col = mix(col0, mix(col1, col2, sin(t+p*PI)), cos(t+p*PI));

    col = pow(col,vec3(0.454545));

    fragColor = vec4(col,1.0);
}
