// Chroma Wave by technochroma
// original code Acid Beach by jarble https://www.shadertoy.com/view/3syyDD
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
#define AA 7 
    vec3 col;
    float t;

    for(int c=0;c<12;c++){
	    vec2 uv = (fragCoord*20.0-iResolution.xy)/iResolution.y;
        t = iTime;
        for(int i=1;i<7;i++)
        {
            uv /= 1.70;
            uv += ceil(col.yx);
        	uv += float(i) + (cos(uv.x)*cos(uv.y)+sin(uv.y)*cos(iTime)+cos(iTime)*cos(uv.x));
        }
     col[c] = (fract(uv.x+uv.y+iTime));
	}

    fragColor = vec4(col,1.0);

    // small bit of anti-aliasing (fixed 2)
    // drop this in voidmain after fragcolor

	vec2 of = vec2(0.3);

    #ifdef AA
	const float aa = float(AA);
    #else
    const float aa = 1.0;


	for(float i = 0.0; i < aa - 1.0; i++) {

		// super-sample around the center of the pixel.
		vec2 p = (-iResolution.xy + 2.0*(uv + of))/iResolution.y;
        col += render(p);
		of *= r(3.14159/8.0);

        	}

	col /= aa;

	col += 0.2*clamp(col, 0.0, 0.5);
	col = pow(col, vec3(1.0/2.2));

    #endif 

}
