// Original shader from: https://bit.ly/2WoLdpT
// Original shader from: https://www.shadertoy.com/view/wtKBRd


  //////////////////////////////// Gaz fractal 35  ad   
  #define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)


  ///////////////// Advice ( Thank you FabriceNeyret2 )
  void mainImage( out vec4 o, vec2 u ){
    float e=0.,g=0.,s, t = .1*iTime, i = 0.; 

    for( o *= 0. ; i++ < 2e2; ) {
        vec3 r = iResolution,
             p = vec3( ( u - r.xy*.5 ) / r.y *g*1.25*0.5 + vec2(.9,0.9) , g += e );

    ////////////////////////////////////// Default case 
   /*  p.xz -= t ;
     p   -= round(p) ;
      */
     //////////////////////////////////////  only one case
       p.xz -= t*0.5;  p.x +=2.3-1.5; p.y -=0.25*t ; // 20 seconds appear
        p.y -= round(p.z);  

      ///////////////////////////////////////  


        p.xz *= 1. + .2* sin( t*31.4 + 9.* (round(g)+p.y) );
        e = s = 9.;

        for(int j=0; j++ < 6; s += s )
           e = s/2e6 + min( e*.95, max( -p.y, abs(length(p)-.26*1.2) ) /s ) ,
           p.y += length(p.xz)*2.*1.3,
           p = .2*1.2 - abs(p*3.*(1.+0.125*sin(iTime*0.15 )  ));

        o += 4e-6/e * ( 1. + .9* clamp(  abs( fract( .6+.9/p.y + vec4(3,2,1,0)/3. ) * 6. - 3. ) - 2., -1.,0.) );
    }

      /////////////////////////////  ad Gaz fractal 35  Union
      vec4 O1=vec4(0);              //vec4 o => O1 ;  vec2 C => u ;
    vec3 p,r=iResolution,
    d=normalize(vec3((u-.5*r.xy)/r.y+vec2(0.75,0.),1.*0.5));  //offset zoomfactor*0.5
    for(float i=1.,g=0.,e,s;        ++i<99.;    // de  ++i<99
        O1.rgb+=mix(vec3(1),H(log(s*s)/5.),.5)*pow(cos(i*i/64.),2.)/e/2e4
    )
    {
        p=g*d-vec3(1.5,-.25,1.3);
        p=R(p,normalize(vec3(1,1,1)),iTime*.1);
        s=3.;
        for(int i=0;
            i++<4;
            p=vec3(2,2,2)-abs(abs(p)*e-vec3(3,5,1))
        )
            s*=e=1.*0.875/clamp(dot(p,p),.1,.6);
        g+=e=min(length(p.xz)-.02,abs(p.y))/s+.001;
     }

     ///////////////////////////////////

       o  += O1 ;  // final color( Jelly fish + Gaz Fractal 35 union)
}
