#define M(a,b)    mat2(a, -a.y, a.x) * b
#define I(z)      vec2((z).x, -(z).y) / dot(z,z)

void mainImage(out vec4 O, vec2 u)
{
    vec2 R = iResolution.xy;

    float t_zoom = iTime * 0.2;  // control the speed of the zoom with this factor
    float zoom = 0.4 + sin(t_zoom) * 0.5;  // compute the zoom factor, which oscillates between 0.5 and 1.5

    float t_rot = iTime * 0.05;  // control the speed of the rotation with this factor
    // compute a rotation matrix
    float angle = t_rot;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));

    vec2 c = zoom * 1.75 * ( rot * (u+u - R) ) / R.y,
         z = c;

    float s = 0.,i=s;

    for (; i < 1.; i+=.005 ) {  

        s -= 1.5* log( pow(2.,-2./3.) * dot(z,z) );
        z = I(M(z,z)) + c;      

    }

    O = i < 1. ?  .6 + .6 * cos( 6.3*i  + vec4(0,23,21,0)  )
               :  vec4( sin(atan(abs(s)/2e2)) );
}
