vec3 WHITE = vec3(1);
vec3 YELLOW = vec3(255, 255, 0)/255.;
vec3 RED = vec3(178, 34, 52)/255.;

float Star(vec2 uv, float size){

    uv.x = abs(uv.x);

    float a = 6.2832/5.;
    float d1 = dot(uv, vec2(sin(a), cos(a)));

    a = 3.* 6.2832/5.;
    float d2 = dot(uv, vec2(sin(a), cos(a)));

    a = 2.* 6.2832/5.;
    float d4 = dot(uv, vec2(sin(a), cos(a)));

    float d = min(max(d1, d2),max(uv.y, d4));

    float w = fwidth(d);

    return smoothstep(-w, w, d-size);

}

vec3 Flag(vec2 uv){

    float w = fwidth(uv.y);

    float size = 0.088;

    vec3 col = mix(YELLOW, RED, Star(uv, size));

    col *= smoothstep(w, -w, abs(uv.y -.5) - .998 + w);

    col *= smoothstep(w, -w, uv.x*0.7 - 0.88 + w);

    col *= smoothstep(w, -w, uv.y*(0.7) - 0.38 + w);

    return col;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float w = fwidth(uv.y);

    float t = (uv.x*5.-2.*iTime+uv.y*3.); 

    uv.y += sin(t)*0.05;

    vec3 col = Flag(uv);

    col *= 0.95+cos(t)*.25;

    fragColor = vec4(col,1.0);
}
