float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

float ndot(vec2 a, vec2 b ) { return a.x*b.x - a.y*b.y; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 8.0 * (sin(iTime) + 64.0)/ 64.0;
    float l = fract(length(uv) * 4.0);

    uv -= 0.5;
    //pulse
    vec2 pr = (2.0*fragCoord-iResolution.xy)/iResolution.y;
	float dr = sdCircle(pr,sin(l*11.70*8.0 + iTime)/8.0);

    //circle
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
	float d = sdCircle(p,sin(l*8.0 + iTime)/8.0);
    d = fract(d);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    col -= abs(l) + smoothstep(0.01,0.2,abs(d));
    col += 0.8 + 0.2*cos(150.0*d);

    // Output to screen
    fragColor = vec4(col,1.0);

}
