float rand(float x) {return fract(sin(x) * 8192.) * 16.;}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 midCen = vec2(iResolution.x * 0.5, iResolution.y * 0.5);

    vec2 shake = vec2(rand(iTime * 1.2345), rand(iTime * 0.655));

    vec2 sun = midCen - shake;

    float sunRadSpe = 32. * iTime;
    float invSunDist = sunRadSpe / distance(fragCoord, sun);

    float it = invSunDist;
    fragColor = vec4(it * 0.25, it * rand(iTime) * 0.0012, it * rand(iTime) * 0.0023, 1.);
}
