#define PI 3.141592

vec3 pal( in float t )
{
    return vec3(0.5) + vec3(0.5) * cos(2.*PI*(vec3(1.0)*t+vec3(.0,.33,.67)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 fragNorm = fragCoord/iResolution.xy;

    vec2 uv = fragNorm * 20. -10.;
    uv.x *= iResolution.x/iResolution.y;
    float angle = dot(uv, uv);//iTime + cos(iTime)/2.;
    uv = vec2(uv.x*cos(angle)-uv.y*sin(angle), uv.x*sin(angle)+uv.y*cos(angle));
    uv = cos(uv);
    uv -= length(uv);
    vec2 p = vec2(uv.x + cos(iTime+uv.x+sin(iTime+uv.y))*0.4, uv.y + sin(iTime+uv.y)*0.3);

    float t = smoothstep(0.1, 1.5, length(p));

    vec3 col = 1. - vec3(t);
    col *= pal(iTime*0.2 + sin(iTime + length(uv))*0.4);
    fragColor = vec4(col,1.0);
}
