#define t iTime
#define r iResolution.xy


#define dotCount 17.

#define xFolds 6.
#define yFolds 7.

#define brightness 0.05
#define bloom .75

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord * 2.0 - r) / r.y;
    uv *= 1.2;    

    float minVal = 999.;
    for (float i = 0.+t*0.03; i < dotCount * (xFolds * yFolds)+t*0.3; i++) {
        float val = abs(uv.y - sin(i * xFolds + cos(t)*0.1)) +
                    abs(uv.x - cos(i * yFolds + sin(t)*0.1));

        if (val < minVal) {
            minVal = val;
        }
    }

    float val = .3/(minVal/brightness);
    val = pow(val,1./bloom);

    vec3 col = val + vec3(0.1,0.2,0.3);

    fragColor = vec4(col, 1.0);
}
