//variation of https://www.shadertoy.com/view/mddyWS
#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))

void mainImage(out vec4 o, vec2 u) {    
    vec3 p = iResolution;  u = (u - p.xy/2.) / p.y;  o *= 0.;

    float i, d, s, a, t = iTime;
    while(i++ < 1e2){
        p = vec3(u * a * .2, 4. - a);

        p.zx *= rot(t);
        p *= d = 30. / dot(p, p);
        p.zy *= rot(dot(p.zx, cos(t * .1 + vec2(11, 0)) ) * .1 + t * 1.);

        s = sin(p.y / 100.) * 10.  +  4.; 
        d = abs(s / d) - .001;

        if(p.z * cos(p.z * .02) < 0.){
            a += d;
            o += .01 / exp(300. * d);
        }

        else{
            a += max(d, 4e-3);
            d < .002 ? o += vec4(1,1,2,0)/1e2 : o;
        }
    }
}
