void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 center = vec2(iMouse.x,iMouse.y);
    float mosaicSize = 30.0;
    float recSide = 90.0;


    vec2 mosaicBlockSize = vec2(mosaicSize,mosaicSize);
    vec2 blockStart = fragCoord;;
    blockStart.x -= mod(blockStart.x,mosaicBlockSize.x);
    blockStart.y -= mod(blockStart.y,mosaicBlockSize.y);
    vec2 texSize = vec2(textureSize(iChannel0,0));
  	vec4 col = texture(iChannel0,blockStart/texSize);
    fragColor = vec4(col);

    vec2 uv = fragCoord / iResolution.xy;
    vec4 texColor = texture(iChannel0, uv);

    if(fragCoord.x<center.x - recSide || fragCoord.x>center.x + recSide || fragCoord.y<center.y - recSide || fragCoord.y>center.y + recSide)
    {
        fragColor = texColor;
    }

}
