void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 p = fragCoord.xy / iResolution.xy;

    float x = exp2(-p.y * abs(fract(iTime * 0.05) - 0.5) * 50.0f);

    float y = exp2(-p.x * abs(fract(iTime * 0.075) - 0.5) * 50.0f);

    fragColor = vec4(step(p.y, y), step(p.x, x), 0.0, 1.0 );


}
