#define r iResolution
#define c(p,a) (1.-step(length(fract(f)-.5+p*vec2(cos(a),sin(a))),.35))

void mainImage( out vec4 o, in vec2 f )
{
    f = (f.xy / r.xy * 2. -1.)*4.;
    f.x*=r.x/r.y;
    float d = c(.0,0.);
    float a = floor(f.x)+floor(f.y);
    vec3 x=vec3(.1,.1,.9);
    o = vec4(mix(x,x.rbg,d)+c(.04,a)-c(.04,a+3.),0.);
}
