#define PI 3.1415926538
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float vTime = fract(iTime);

    vec2 uv = fragCoord/ iResolution.xy; 
    //uv.x *= iResolution.x/iResolution.y;
    uv -= 0.5;

    vec2 uv2 = fragCoord/ iResolution.xy; 
    //uv2.x *= iResolution.x/iResolution.y;
    uv2 -= .5;

    uv.x = uv.x * cos(PI/ 5.0) - uv.y * sin(PI/5.0);
    uv.y = uv.x * sin(PI/5.0) + uv.y * cos(PI/5.0);

    uv2.x = uv2.x * cos(-PI/ 5.0) - uv2.y * sin(-PI/5.0);
    uv2.y = uv2.x * sin(-PI/5.0) + uv2.y * cos(-PI/5.0);

    uv.y  *= 0.3;
    uv2.y *= 0.3;


    float strength = 0.015 / length(uv) - 0.1;
    strength += 0.015 / length(uv2) - 0.1;


    // Output to screen
    fragColor = vec4(strength, strength, strength + 0.4 ,1.0);
}
