void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;  
    vec4 tex = texture(iChannel0,uv);

    //highlight
    float s = 3.0; //scale
    float p = (uv.x - 0.5)*s + 0.5;    
    p-=(fract(iTime*0.5)*1.0 - 0.5) * s *1.5;
    float h = 1.0 - abs(p*2.0 - 1.0);
    h = pow(h,3.0); //fade
    h = clamp(h, 0.0, 1.0);

    //mask
    vec3 targ = vec3(0.3,0.11,0.47); //target mask color
    float m = distance(targ, tex.rgb); 
    float e = 0.29; //edge
    float f = 0.1;  //feather
    m = smoothstep(e + f, e - f, m);
    m*= 2.0; //bias
    m = clamp(m, 0.0, 1.0);

    vec3 col = tex.rgb + m*h*vec3(1.0, 0.75, 0.0);

    //quick debug
    //col = vec3(h);
    //col = vec3(m);

    fragColor = vec4(col,1.0);
}
