#define S(a, b, x) smoothstep(a, b, x)

struct MBAnimationParams {
    float radius;
    float rate;
    vec2 phase;
};
const int MB_COUNT = 4;
const MBAnimationParams MBs[MB_COUNT] = MBAnimationParams[](
    MBAnimationParams(0.6, 1./3., vec2(5., 1.5)),
    MBAnimationParams(0.8, 1.3/3., vec2(3., 4.5)),
    MBAnimationParams(1., 1.5/3., vec2(4., 6.)),
    MBAnimationParams(1.2, 1.7/3., vec2(2., 9.))
);

const float V = 0.7; // potential (v) at which color gradients start
const float POW = 2.; // potential decay

const float NOISE_TIME_FACTOR = 0.1;
const float NOISE_XY_FACTOR = 1.2;
const float NOISE_MAG = 1.;


vec4 v4(float val) {
    return vec4(val, val, val, val);
}

vec3 v3(float val) {
    return vec3(val, val, val);
}


// Classic Perlin 3D Noise 
// by Stefan Gustavson
//
vec4 permute(vec4 x) {
    return mod(((x*34.)+1.)*x, 289.);
}

vec4 taylorInvSqrt(vec4 r) {
    return 1.79284291400159 - 0.85373472095314 * r;
}

vec3 fade(vec3 t) {
    return t*t*t*(t*(t*6.-15.)+10.);
}

float cnoise(vec3 P) {
    vec3 Pi0 = floor(P); // Integer part for indexing
    vec3 Pi1 = Pi0 + vec3(1.); // Integer part + 1
    Pi0 = mod(Pi0, 289.);
    Pi1 = mod(Pi1, 289.);
    vec3 Pf0 = fract(P); // Fractional part for interpolation
    vec3 Pf1 = Pf0 - vec3(1.); // Fractional part - 1.
    vec4 ix = vec4(Pi0.x, Pi1.x, Pi0.x, Pi1.x);
    vec4 iy = vec4(Pi0.yy, Pi1.yy);
    vec4 iz0 = Pi0.zzzz;
    vec4 iz1 = Pi1.zzzz;

    vec4 ixy = permute(permute(ix) + iy);
    vec4 ixy0 = permute(ixy + iz0);
    vec4 ixy1 = permute(ixy + iz1);

    vec4 gx0 = ixy0 / 7.;
    vec4 gy0 = fract(floor(gx0) / 7.) - 0.5;
    gx0 = fract(gx0);
    vec4 gz0 = vec4(0.5) - abs(gx0) - abs(gy0);
    vec4 sz0 = step(gz0, vec4(0.));
    gx0 -= sz0 * (step(0., gx0) - 0.5);
    gy0 -= sz0 * (step(0., gy0) - 0.5);

    vec4 gx1 = ixy1 / 7.;
    vec4 gy1 = fract(floor(gx1) / 7.) - 0.5;
    gx1 = fract(gx1);
    vec4 gz1 = vec4(0.5) - abs(gx1) - abs(gy1);
    vec4 sz1 = step(gz1, vec4(0.));
    gx1 -= sz1 * (step(0., gx1) - 0.5);
    gy1 -= sz1 * (step(0., gy1) - 0.5);

    vec3 g000 = vec3(gx0.x,gy0.x,gz0.x);
    vec3 g100 = vec3(gx0.y,gy0.y,gz0.y);
    vec3 g010 = vec3(gx0.z,gy0.z,gz0.z);
    vec3 g110 = vec3(gx0.w,gy0.w,gz0.w);
    vec3 g001 = vec3(gx1.x,gy1.x,gz1.x);
    vec3 g101 = vec3(gx1.y,gy1.y,gz1.y);
    vec3 g011 = vec3(gx1.z,gy1.z,gz1.z);
    vec3 g111 = vec3(gx1.w,gy1.w,gz1.w);

    vec4 norm0 = taylorInvSqrt(vec4(dot(g000, g000), dot(g010, g010), dot(g100, g100), dot(g110, g110)));
    g000 *= norm0.x;
    g010 *= norm0.y;
    g100 *= norm0.z;
    g110 *= norm0.w;
    vec4 norm1 = taylorInvSqrt(vec4(dot(g001, g001), dot(g011, g011), dot(g101, g101), dot(g111, g111)));
    g001 *= norm1.x;
    g011 *= norm1.y;
    g101 *= norm1.z;
    g111 *= norm1.w;

    float n000 = dot(g000, Pf0);
    float n100 = dot(g100, vec3(Pf1.x, Pf0.yz));
    float n010 = dot(g010, vec3(Pf0.x, Pf1.y, Pf0.z));
    float n110 = dot(g110, vec3(Pf1.xy, Pf0.z));
    float n001 = dot(g001, vec3(Pf0.xy, Pf1.z));
    float n101 = dot(g101, vec3(Pf1.x, Pf0.y, Pf1.z));
    float n011 = dot(g011, vec3(Pf0.x, Pf1.yz));
    float n111 = dot(g111, Pf1);

    vec3 fade_xyz = fade(Pf0);
    vec4 n_z = mix(vec4(n000, n100, n010, n110), vec4(n001, n101, n011, n111), fade_xyz.z);
    vec2 n_yz = mix(n_z.xy, n_z.zw, fade_xyz.y);
    float n_xyz = mix(n_yz.x, n_yz.y, fade_xyz.x); 
    return 2.2 * n_xyz;
}

// End of Perlin Noise Code

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // calc uv of frag and mouse
	vec2 uv = (-iResolution.xy+2.0*fragCoord.xy)/iResolution.y;
    vec2 uvMouse = (-iResolution.xy+2.*iMouse.xy)/iResolution.y;

    // iterate through metaballs ("mb"), summing the potential at uv
    float v = 0.;	
    for (int i = 0; i < MB_COUNT; i += 1) {
        MBAnimationParams mb = MBs[i];

        // get parameterized mb position
        vec2 c = mb.radius * sin( iTime*mb.rate + mb.phase);

        // add potential to running sum
        v += 0.1 / (float(MB_COUNT + 1) * pow(S(0., 1., length(uv - c)), POW));
    }

    // add potential for mouse metaball
    v += 0.1 / (float(MB_COUNT + 1) * pow(S(0., 1., length(uv - uvMouse)), POW));

    // add ambient perlin noise for random blobs 
    v += cnoise(vec3(uv * NOISE_XY_FACTOR, iTime * NOISE_TIME_FACTOR)) * NOISE_MAG;

    // build color from sectioning the gradient into ranges (read inner to outer)
    fragColor = mix(
        // inside metaball
        mix(
            mix(
                vec4(.90, .97, 1., 1.), // off-white
                vec4(.8, 1., 1., 1.), // blue
                S(V, V - 0.1, v)
            ),
            vec4(.8, .7, 1., 1.), // purple
            S(V - .1, V - .199, v)
        ),
        // outside metaball
        mix(
            vec4(.2, .2, .6, 1.), // dark blue
            vec4(.1, .1, .1, 1.), // near black
            S(V - 0.2, V - 0.3, v)
        ),
        S(V - .199, V - .201, v)
    );
}
