void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Définir la résolution de l'écran
    vec2 resolution = iResolution.xy;

    // Définir la couleur de base
    vec4 base_color = vec4(0.0, 0.0, 0.0, 1.0);

    // Appliquer un effet de ligne verticale
    float line_y = mod(fragCoord.y, 10.0);
    float line_x = mod(fragCoord.x, 15.0);
    float line_x_offset = (cos(iTime * 5.0) + 1.0) * 0.5 * 5.0;
    float line_y_offset = (cos(iTime * 2.0) + 1.0) * 0.5 * 5.0;

    if (line_y + line_y_offset < 1.0 || line_y + line_y_offset > 9.0) {
        base_color = vec4(1.0);
    }
    if (line_x + line_x_offset < 1.0 || line_x + line_x_offset > 14.0) {
        base_color = vec4(1.0);
    }

    // Appliquer la couleur finale
    fragColor = base_color;
}
