const float MAX_ITER = 80.0;
const float AA_STR = 1.0;
const float AA_MAX = 6.0;

vec3 palette(in float t) {
    vec3 a = vec3(0.5, 0.5, 0.5) - 0.15;
    vec3 b = vec3(0.5, 0.5, 0.5) + 0.15;
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.4, 0.6, 0.7);
    return a + b * cos(6.28318 * (c * t + d));
}

struct Result {
    float iterations;
    float d;
};

Result map(in vec2 z, in vec2 c) {
    Result r;
    r.d = 9999.0;

    for (r.iterations = 0.0; r.iterations < MAX_ITER; r.iterations++) {
        z = cplxSquare(z) + c;

        float t = 0.05 * iTime;
        float d = 0.1 / (z.x * sin(t) + z.y * cos(t));
        if (d < r.d) {
            r.d = d;
        }
    }
    return r;
}

vec3 mainPass(in vec2 fragCoord) {
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    uv *= 0.95;

    float t = 0.5 * iTime;
    vec2 c = vec2(-0.8, 0.16);
    c += 0.01 * vec2(cos(t), sin(t));
    Result r = map(uv, c);

    vec3 color = palette(atan(r.d));

    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 color = mainPass(fragCoord);

    // adaptive AA
    float aaLevel = min(floor(AA_STR * dot2(fwidth(color))), AA_MAX);
    for (float i = 1.0; i <= AA_MAX; i++) {
        if (i > aaLevel) break;
        color += mainPass(fragCoord + hash21(i) - 0.5);
    }
    color /= (aaLevel + 1.0);
    color = gamma(color);
    fragColor = vec4(color, 1.0);
}

float dot2(vec2 v) { return dot(v, v); }
float dot2(vec3 v) { return dot(v, v); }

vec2 cplxSquare(vec2 c) {
    return vec2(c.x * c.x - c.y * c.y, 2.0 * c.x * c.y);
}

vec3 gamma(in vec3 color) {
    return pow(max(color, 0.0), vec3(1.0 / 2.2));
}

vec2 hash21(float p) {
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}
