void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse = iMouse.xy / iResolution.xy;

    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvx1 = (fragCoord + vec2(1, 0))/iResolution.xy;
    vec2 uvx2 = (fragCoord + vec2(-1, 0))/iResolution.xy;
    vec2 uvy1 = (fragCoord + vec2(0, 1))/iResolution.xy;
    vec2 uvy2 = (fragCoord + vec2(0, -1))/iResolution.xy;

    vec4 texColx1 = texture(iChannel0, uvx1);
    vec4 texColx2 = texture(iChannel0, uvx2);
    vec4 texColy1 = texture(iChannel0, uvy1);
    vec4 texColy2 = texture(iChannel0, uvy2);

    float xVal1 = (texColx1.x + texColx1.y + texColx1.z) / 3.0;
    float xVal2 = (texColx2.x + texColx2.y + texColx2.z) / 3.0;
    float yVal1 = (texColy1.x + texColy1.y + texColy1.z) / 3.0;
    float yVal2 = (texColy2.x + texColy2.y + texColy2.z) / 3.0;

    float dx = abs(xVal1 - xVal2);
    float dy = abs(yVal1 - yVal2);
    float gr = sqrt(pow(dx, 2.) + pow(dy, 2.));

    vec3 col = vec3(0.);

    if(gr > .2){
        col.r = 1.;
    } else if(gr > .1){
        col.g = 1.;

        for(int x = -1; x <= 1; x++){
            for(int y = -1; y <= 1; y++){
                if(x != y){
                    uvx1 = (fragCoord + vec2(1, 0) + vec2(x, y))/iResolution.xy;
                    uvx2 = (fragCoord + vec2(-1, 0) + vec2(x, y))/iResolution.xy;
                    uvy1 = (fragCoord + vec2(0, 1) + vec2(x, y))/iResolution.xy;
                    uvy2 = (fragCoord + vec2(0, -1) + vec2(x, y))/iResolution.xy;

                    texColx1 = texture(iChannel0, uvx1);
                    texColx2 = texture(iChannel0, uvx2);
                    texColy1 = texture(iChannel0, uvy1);
                    texColy2 = texture(iChannel0, uvy2);

                    xVal1 = (texColx1.x + texColx1.y + texColx1.z) / 3.0;
                    xVal2 = (texColx2.x + texColx2.y + texColx2.z) / 3.0;
                    yVal1 = (texColy1.x + texColy1.y + texColy1.z) / 3.0;
                    yVal2 = (texColy2.x + texColy2.y + texColy2.z) / 3.0;

                    dx = abs(xVal1 - xVal2);
                    dy = abs(yVal1 - yVal2);
                    gr = sqrt(pow(dx, 2.) + pow(dy, 2.));

                    if(gr > .2){
                        col.b = 1.;
                        x = 3;
                        y = 3;
                    }

                }
            }
        }









    }

    if(uv.x > mouse.x - .001 && uv.x < mouse.x + .001){
        fragColor = vec4(0., 0., 1., 1.0);
    } else if(uv.x < mouse.x){
        fragColor = vec4(vec3(col.r), 1.0);
    } else {
        fragColor = vec4(vec3(col.r + col.b), 1.0);
    }

}
