vec3 palette(float t)
{
    vec3 a = vec3(0.9, 0.2, 0.2);    // Rouge
    vec3 b = vec3(0.9, 0.5, 0.2);    // Orange
    vec3 c = vec3(0.9, 0.9, 0.2);    // Jaune
    vec3 d = vec3(0.5, 0.9, 0.2);    // Vert
    vec3 e = vec3(0.2, 0.9, 0.9);    // Cyan
    vec3 f = vec3(0.2, 0.2, 0.9);    // Bleu
    vec3 g = vec3(0.7, 0.2, 0.9);    // Violet

    return a + b * cos(6.28318 * (c * t + d)) +
           e * cos(6.28318 * (f * t + g));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 5.0; i++) {
        uv = fract(uv * 1.25) - 0.5;

        float d = length(uv) * exp(-length(uv0));
        vec3 col = palette(length(uv0) + i * 0.4 + iTime * 0.4);
        d = sin(d * 10.0 + iTime * 0.5) / 10.0;
        d = abs(d);
        d = pow(0.01 / d, 3.0);
        d = d / 5.0;

        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
