#define PI 3.14159265359
#define f 0.2

vec2 rotate(vec2 uv, float th)
{
    return mat2(cos(th), sin(th), -sin(th), cos(th)) *uv;
}

vec3 sdfCircle(vec2 uv, float r) {

  float x = uv.x - 0.2*cos((2.0*PI*f)*(iTime));
  float y = uv.y;

  vec2 rot = rotate(vec2(x,y), iTime);

  float d = length(rot) - r;

  return d > 0. ? vec3(0.) : 0.5 + 0.5 * cos(iTime + rot.xyx + vec3(0,2,4));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 uv = fragCoord/iResolution.xy; // <0,1>
  uv -= 0.5;
  uv.x *= iResolution.x/iResolution.y; // fix aspect ratio
  vec3 col = vec3(0.3);
  vec2 rot = rotate(uv, iTime);
  if(rot.x < (0.2*cos((2.0*PI*f)*(iTime)))){
      col = sdfCircle(rot, pow(0.6*sin((2.0*PI*f)*(iTime)), 2.0));
  }
  else{
      col = sdfCircle(rot, pow(0.6*cos((2.0*PI*f)*(iTime)), 2.0));
  }


  // Output to screen
  fragColor = vec4(col,1.0);
}
