float sn(float f) {
    return 0.5 + 0.5 * sin(f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float speed = 10.0;

    vec3 col = vec3(1.0)
        + 0.5 * vec3(1.0, 0.1, 0.5) * floor(sin((cos(iTime) * uv.x) * 100.0 + iTime * speed))
        + 0.2 * vec3(0.0, 0.1, 0.5) * floor(sin((sin(iTime) * uv.y) * 100.0 - iTime * speed));
    fragColor = vec4(col,1.0);
}
