// https://youtu.be/f4s1h2YETNY
// https://iquilezles.org/articles/palettes/
// http://dev.thi.ng/gradients/
// https://www.shadertoy.com/view/mtyGWy
// https://www.shadertoy.com/view/dldyz4

vec3 palette(float t) {
    // [[0.338 0.308 0.418] [0.888 0.708 1.778] [0.398 0.508 -0.382] [-0.392 -0.500 -0.422]]
    t = clamp(t, 0.0, 1.0);
    vec3 a = vec3(0.338, 0.308, 0.418);
    vec3 b = vec3(0.888, 0.708, 1.778);
    vec3 c = vec3(0.398, 0.508, -0.382);
    vec3 d = vec3(-0.392, -0.500, -0.422);

    return a + b*cos(6.28318 * (c * t + d));
}

mat2 rotate(float ang) {
    float s = sin(ang);
    float c = cos(ang);
    return mat2(c, -s, s, c);
}

float getNurb(vec2 uv, float iTime, int sinCosX, int sinCosY, float xMultiplier, float yMultiplier, float size) { 

    if (sinCosX == 0) {
        uv.x += sin(iTime * xMultiplier);
    } else {
        uv.x += cos(iTime * xMultiplier);
    }

    if (sinCosY == 0) {
        uv.y += sin(iTime * yMultiplier);
    } else {
        uv.y += cos(iTime * yMultiplier);
    }

    return (size - length(uv)) / size;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec3 finalColor;
    float scalar = 2.8;
    vec4 ch0 = texture(iChannel0, rotate(0.05 * iTime) * uv * (0.5 * sin(iTime * 0.1) + 0.75));

    finalColor += getNurb(uv, iTime * 1.3, 0, 1, 0.3, -1.1, 1.5) / scalar;
    finalColor += getNurb(uv, iTime / 2.1, 1, 0, 0.5, 1.7, 1.4) / scalar;
    finalColor += getNurb(uv, iTime * 1.2, 0, 1, 0.7, -2.1, 1.6) / scalar;
    finalColor += getNurb(uv, iTime / 2.2, 1, 0, -1.1, 0.7, 1.3) / scalar;
    finalColor += getNurb(uv, iTime * 1.7, 1, 0, -1.7, -0.7, 1.8) / scalar;

    fragColor = vec4(palette(finalColor.x * 2.0 - ch0.x), 1.0);
}
