// Code golfing https://www.shadertoy.com/view/cltfW2

/* 253 chars */
#define S * smoothstep(0., 1.5*fwidth

void mainImage( out vec4 Q, vec2 U)
{
    vec2 R = iResolution.xy, 
    V = U = (U+U-R)/R.y;
    V.y++;

    float N = 2.*ceil(6.+4.*cos(.6*iTime)),
          a = ++N*atan(.5*dot(V,V)/U.x) / 3.14;

    Q = 0.*Q + 2.*ceil(abs(a))/++N
               S(U.x), 1.-length(U)) 
               S(a), abs(a-round(a)));
}



