#define STEP_SIZE 32.0
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float y1 = floor(fragCoord.x / STEP_SIZE) * STEP_SIZE;
    float x1 = smoothstep(0.0,1.0, (y1+sin(iTime*0.5)*iResolution.x)/iResolution.x);
    fragColor = vec4(vec3(step(uv.y,x1)),1.0);
}
