void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = fragCoord/iResolution.xy;

   vec4 tex = texture(iChannel0, uv.xy);

   vec4 pallet = texture(iChannel1, uv.xy);

   vec3 color = vec3(0.0);

   float closest = 0.9;

   float numberOfColors = 128.0;

   float closeIndex = -1.0;

   for( float i = 1.0 / numberOfColors; i <= 1.0; i += 1.0 / numberOfColors)
   {
       pallet = texture(iChannel1, vec2(0.0, i));
       vec3 dtex = tex.rgb - pallet.rgb;
       float distance = length(dtex);

       if (distance < closest)
       {
           closest = distance;
           closeIndex = i;
       }
   }

   pallet = texture(iChannel1, vec2(0.0, closeIndex));

   color += pallet.rgb;

   fragColor = vec4(color,1.0);
}
