mat2 rot(float a){
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}
mat3 rotX(float a){
    return mat3(1.,0.,0.,0.,cos(a),sin(a),0.,-sin(a),cos(a));}
mat3 rotY(float a){
    return mat3(cos(a),0.,sin(a),0.,1.,0.,-sin(a),0.,cos(a));}
float sabs(float x){
    return sqrt(x*x+7.5);}
float smin(float x,float y){
    return (x+y-sabs(x-y))*0.5;}
float smax(float x,float y){
    return (x+y+sabs(x-y))*0.5;}

float sphere(vec3 p,float r){ return length(p)-r;}
float box(vec3 p,vec3 b){
    vec3 q=abs(p)-b;
    return length(max(q,0.))+min(max(max(q.x,q.y),q.z),0.);}

struct objects{
    float d1;//sphere
    float d2;//box
    float d3;//box in
};

vec3 hash3(float t){
    return vec3(fract(sin(t*86996.)*8757.55),fract(sin(t*18696.)*8277.95),fract(sin(t*45486.)*7653.73))*2.-1.;}

vec4 point(float t, vec3 p){
    vec3 r=hash3(t)*8.;
    float d21=box(p*rotX(r.x)*rotY(r.y),vec3(25.));
    float d22=box((p-r.yxz)*rotX(r.z)*rotY(r.x),vec3(25.));
    float d23=box((p-r.yzx)*rotX(r.y)*rotY(r.z),vec3(25.));
    float d24=box((p-r.zxy)*rotX(r.y)*rotY(r.y),vec3(25.));
    float d2=smin(smin(smin(d21,d22),d23),d24);
    return vec4(r,d2);
}

vec3 la(float tm,vec3 dr){
    //float tm=mod(iTime,12.);
    vec3 r;
    if(tm<3.) r=mix(vec3(0.),dr.yxz,tm/3.);
    else if(tm<6.) r=mix(dr.yxz,dr.yzx,(tm-3.)/3.);
    else if(tm<9.) r=mix(dr.yzx,dr.zxy,(tm-6.)/3.);
    else r=mix(dr.zxy,vec3(0.),(tm-9.)/3.);
    return r;}

objects ob(vec3 p){
    vec4 dr=point(floor(iTime/12.),p);//box(p*rotX(87.96)*rotY(9.97),vec3(10.));
    float d2=dr.w;
    float tm=mod(iTime,12.);
    vec3 r=la(tm,dr.xyz);
    float d3=(fract(sin(iTime*20.))>0.96) ? 9999. : box((p)*rotX(-iTime*0.1)*rotY(-iTime*0.2),vec3(1.4));
    //float d32=(fract(pow(iTime+10.,2.))>0.6) ? 9999. : box((p-r.zyx)*rotX(-iTime*0.1)*rotY(-iTime*0.2),vec3(0.5));
    //d3=smin(abs(d3)-0.01,d32);
    float d4=box((p-r)*rotX(-iTime*0.1)*rotY(-iTime*0.2),vec3(12.));
    float d1=sphere(p-r-vec3(sin(tm)*0.02,0.,0.)-sin(p*30.)*0.05,1.);
    d2=max(-d4,d2);
    objects ob;
    ob.d1=d1;
    ob.d2=d2;
    ob.d3=abs(d3)-0.01;
    return ob;
}

vec2 map(vec3 p){
    objects o=ob(p);
    float dmin=smin(o.d1,o.d2);
    dmin=smin(dmin,o.d3);
    float m=0.;
    if(abs(dmin)<0.005){
        if(o.d1<min(o.d2,o.d3)) m=1.;
        else m=2.;
    }
    return vec2(dmin,m);
}

vec3 matcol(float m,vec3 p){
    vec3 col;
    if(m>1.) col=pow(vec3(0.1,0.8,0.9),vec3(0.7));
    else if(m>0.) col=pow(vec3(0.8,0.2,0.6),vec3(0.9));
    return col;
}

vec3 bg(vec3 rd){
    vec2 p=rd.xy;
    p.x=abs(p.x)-0.3;
    float l=smoothstep(0.01,0.,abs(p.y+p.x*5.))*smoothstep(0.2,0.,abs(p.y+(fract(iTime*1.5))))*smoothstep(0.5,-0.5,p.y);
    return vec3(l*l*l+0.02,l*l+0.02,sin(l*10.)+0.02);
}

