//https://www.shadertoy.com/view/MtX3Ws
vec2 csqr( vec2 a )  { return vec2( a.x*a.x - a.y*a.y, 2.*a.x*a.y  ); }
float marble(in vec3 p) {

	float res = 0.;

    vec3 c = p;
	for (int i = 0; i < 10; ++i) {
        p =.7*abs(p)/dot(p,p) -.7;
        p.yz= csqr(p.yz);
        p=p.zxy;
        res += exp(-19. * abs(dot(p,c)));

	}
	return res/2.;
}

vec4 raymarch(vec3 ro,vec3 rd){
    vec3 p=ro;
    float l=0.;
    vec2 d=vec2(10.,0.);
    float c=0.;
    float m=0.;
    vec3 col=vec3(0.);
    for(int i=0;i<99;i++){
        p=ro+rd*l;
        transform(p);
        d=map(p);
        l+=max(abs(d.x),0.001)*0.5;
        if(abs(d.x)<0.05){
            c+=0.001;
            m=d.y;
            col+=matcol(m,p)*c;
        }
        if(d.x<0.){ float a=marble(p);a=pow(a,0.8);col+=vec3(a*a,a*a*a,a)*0.08;}
    }
    transform(p);
    float mlast=map(p).y;
    if(mlast<0.5)col=mix(bg(rd),col,c*10.);//mix(vec3(0.1,sin(p.z),atan(p.y,p.x))/l*10.,col,0.1);
    return vec4(col,l);
}

vec3 cam(vec2 uv, vec3 ro,vec3 tar){
    vec3 ff=normalize(tar-ro);
    vec3 uu=vec3(0.,1.,0.);
    vec3 rr=normalize(cross(ff,uu));
    uu=cross(rr,ff);
    return normalize(uv.x*rr+uv.y*uu+ff);}

