#define TAU 6.2831
vec3 hash3(float t){
    return vec3(fract(sin(t*86996.)*8757.55),fract(sin(t*18696.)*8277.95),fract(sin(t*45486.)*7653.73))*2.-1.;}


float B(float t){
    t=fract(t);
    return sin(sin(TAU*8.0*t))*exp(-pow(t,10.));
}

float N(float t,float r){
    t=fract(t+0.3);
    return sin(TAU*28.0*t)*sin(t*TAU*22.)*exp(-pow(t,10.));
}

float M(float t,float r){
    t=fract(t*0.6+0.5);
    return sin(TAU*710.0)*sin(t*TAU*102.*r)*abs(sin(-pow(t,0.3)));
}

float L(float t,float r){
    t=fract(t+r);
    return sin(TAU*160.0)*sin(t*TAU*812.*r)*abs(sin(-pow(t,0.3)));
}

float P(float t){
    return fract(sin(t*30.));
}


vec2 mainSound( int samp, float time )
{
    vec3 r=hash3(time);
    float t1=step(0.98,P(time));
    float t2=step(0.95,P(time));
    float t3=step(0.96,P(time));
    float n=B(time);
    n+=N(time,r.z*r.x);
    n+=M(time,r.x);
    n+=L(time,0.8+r.x);
    n+=M(time*2.,r.z)*t2;
    n+=M(time*4.,r.x)*t3;
    n+=M(time*8.,n)*r.x;
    n+=M(time*16.,r.y+n)*t1;
    n+=L(time,r.x)*t3*0.1;
    n+=N(time*1.5,r.x);
    return vec2(n);
}
