void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.0 - iResolution.xy)/iResolution.y;



    float d = length(uv.x*.001*uv.y*0.001);

    d = smoothstep(0.495,0.5+tan(iTime*100.0*uv.x*uv.y)*-uv.y*uv.x*-uv.x,d);




    fragColor = vec4(d/.3,d*sin(iTime),d*sin(pow(iTime,uv.x*uv.y*70.0*-uv.x)) ,1.0);
}
