vec3 palette( float t ) {
    vec3 a = vec3(0.40, 0.40, 0.43);
    vec3 b = vec3(0.53, 0.40, 0.33);
    vec3 c = vec3(0.83, 0.35, 0.81);
    vec3 d = vec3(0.55,0.32,0.65);

    return a + b*sin(3.1415*3.1415*(c*t+d)/1.5 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / (iResolution.y);
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    float shift = 3.1415/1.;
    float bpm = 60.;
    float delay = bpm/60.;
    float speed = 1.25;

    for (float i = 0.0; i < 6.0; i++) {
        uv = fract(uv * 1.5) - 0.5;
        float d = length(uv) * exp(-length(uv)*length(uv) + abs(sin(length(uv0))));

        vec3 col = palette(length(uv0) + (i+1.)*1.5 - iTime*.25 + sin(iTime*delay*3.141526/1.)*sin(iTime*delay*3.141526/1.)/4.);

        d = sin(d*12. - iTime*0.8*delay*speed + abs(sin(iTime*delay*3.141526/1.)*(1.275*speed/delay)))/1.0 + abs(sin(iTime*delay*3.141526/4.)/2.0);
        d = abs(d);
        d = (d*d) + (d*5.);
        d = 1.*sin(1./(d));

        finalColor += (col * ((d)))/2.;
    }

    fragColor = vec4(finalColor, 1.0);
}
