float gamma = 2.2;

vec3 saturate(vec3 x){return min(vec3(1.0), max(vec3(0.0), x));}

vec3 ACESFilm(vec3 x){
    float a = 2.51;
    float b = 0.03;
    float c = 2.43;
    float d = 0.59;
    float e = 0.14;
    return saturate((x*(a*x+b))/(x*(c*x+d)+e));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 data = texture(iChannel0,uv);
    vec3 c = data.xyz/data.w;
    fragColor = vec4(pow(ACESFilm(c),vec3(1.0/gamma)),1.0);
}

//Thanks to Inigo Quilez for the PRNG and normal function


int seed = 3;
float aperture_size = 3.0;
float light = 0.0;
vec3 skycol = vec3(0.1);
float bri = 0.9;
vec3 rgb;
float refl;
vec3 newdir;
int frand(void) { seed = seed*0x343fd+0x269ec3; return (seed>>16)&32767; }
float rand(void) { return float(frand())/32767.0; }
vec3 point;
float hash1( uint n ) {
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return float( n & uint(0x7fffffffU))/float(0x7fffffff);
}


float sde;
vec3 pos;
float maxl = 10000.0;


float plane_sde(vec3 ray, vec3 nor, float planey, vec3 col, float l, float ref){
    if (dot(ray,nor)+planey < sde){
        sde = dot(ray,nor)+planey;
        refl = ref;
        rgb = col;
        light = l;
        if (light == 1.0){
            if(abs(ray.x)>10.0 || abs(ray.z-80.0)>10.0){
                light = 0.0;
                rgb = vec3(bri);
            }
        }
        //if (nor.y==1.0 && mod(mod(round(ray.x/10.0),2.0)+mod(round(ray.z/10.0),2.0),2.0)<1.0){
            //rgb = vec3(0.1);
        //}
    }
    return dot(ray,nor)+planey;
}


float sphere_sde(vec3 ray, vec3 position, float radius, vec3 col, float l, float ref){
    if (length(position-ray)-radius < sde){
        sde = length(position-ray)-radius;
        refl = ref;
        rgb = col;
        light = l;
    }
    return length(position-ray)-radius;
}


float calc_sdf(vec3 ray){
    sde = maxl;
    sphere_sde(ray,vec3 (-21.0,0.0,80.0),10.0, vec3(bri,0.0,0.0),0.0,0.0);
    sphere_sde(ray,vec3 (0.0,-5.0,80.0),5.0, vec3(bri),0.0,0.7);
    plane_sde(ray,vec3(0,1,0),10.0, vec3(bri,bri,bri),0.0,0.3);
    sphere_sde(ray,vec3 (10.0,-2.0,100.0),8.0, vec3(0,bri,bri),0.0,0.0);
    sphere_sde(ray,vec3 (21.0,0.0,80.0),10.0, vec3(bri,bri,bri),0.0,0.05);
    sphere_sde(ray,vec3 (50.0,90.0,50.0),20.0, vec3(10),1.0,0.0);
    return sde;
}


vec3 calcNormal( in vec3 p ) // for function f(p)
{
    const float eps = 0.0001; // or some other value
    const vec2 h = vec2(eps,0);
    return normalize( vec3(calc_sdf(p+h.xyy) - calc_sdf(p-h.xyy),
                           calc_sdf(p+h.yxy) - calc_sdf(p-h.yxy),
                           calc_sdf(p+h.yyx) - calc_sdf(p-h.yyx) ) );
}


vec4 raycol(vec3 orig, vec3 dir, float ml){
    float len = 0.0;
    light = 0.0;
    sde = ml;
    float steps = 0.0;
    pos = orig;

    while (len < ml && sde > 0.01){
        sde = abs(calc_sdf(pos));
        pos += dir*sde;
        len += sde;
        steps += 1.0;
    }
    return vec4(pos,steps);
}


vec3 sphrand(){
    point = vec3(2.0);
    while(length(point)>=1.0){
        point = vec3(rand()*2.0-1.0,rand()*2.0-1.0,rand()*2.0-1.0);
    }
    return point;
}


vec3 diffuse(vec3 o, vec3 oldrgb){
    vec3 fcol = vec3(1);
    vec3 c;
    sde = 0.01;
    float r = rand();
    float pref = refl;
    vec3 offset = sphrand();
    vec3 pre = oldrgb;
    vec3 normal = calcNormal(o.xyz);
    vec3 inter = o.xyz+normal/10.0;
    newdir = sphrand();
    if (dot(newdir,normal)<0.0){
        newdir *= -1.0;
    }
    float contrib = max(dot(normal,normalize(newdir)),0.0);
    fcol *= pre;
    newdir = normalize(newdir);
    raycol(inter,newdir,1000.0);
    rgb *= contrib*2.0;
    if (sde>0.01){
        c += fcol*skycol;
    } else{
        if (light == 1.0){
            c += fcol*rgb;
        }
    }
    return c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 newfrag = vec2(fragCoord.x+iResolution.x/2.0,fragCoord.y+iResolution.y/2.0); 
    uvec2 p = uvec2((newfrag-.5*iResolution.xy)/iResolution.y*1000.0);
    seed = int(round(hash1( p.x + 1920U*p.y + (1920U*1080U))*100.0*(float(iFrame)+1.0)));
    vec2 th = fragCoord/iResolution.xy;
    vec3 colour = vec3(0);
    for (int i = 0; i<1; i++){
        vec2 ap = sphrand().xy;
        float contrib = 1.0;
        vec3 pos = vec3(ap.x*aperture_size,ap.y*aperture_size+10.0,-5.0);
        vec3 opos = vec3(0,10.0,-5.0);
        vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y+sphrand().xy/iResolution.xy;
        vec3 odir = normalize(vec3(uv.x,uv.y,1.0))*85.0+opos;
        vec3 direction = normalize(odir-pos);
        vec4 o;
        float steps;
        o = raycol(pos,direction,500.0);
        float oref = refl;
        vec3 oldrgb = rgb;
        newdir = direction;
        float bounces = 0.0;
        if (sde <= 0.01){
            if(light == 0.0){
                if (oref<1.0){
                    for (int j = 0; j<8; j++){
                        colour += diffuse(o.xyz,oldrgb)/8.0*(1.0-oref);
                    }
                }
            } else{
                colour += rgb;
            }
            if(oref>0.0){
                vec3 normal = calcNormal(o.xyz);
                float contrib = max(dot(normal,reflect(direction,normal)),0.0);
                o = raycol(o.xyz+calcNormal(o.xyz)/10.0,reflect(direction,calcNormal(o.xyz)),1000.0);
                oldrgb = rgb*contrib;
                if(sde<=0.01){
                    if(light==0.0){
                        for(int j = 0; j<8; j++){
                            colour += diffuse(o.xyz,oldrgb)/8.0*oref;
                        }
                    } else{
                        colour += rgb*oref;
                    }
                } else{
                    colour += skycol*oref;
                }
            }
        }else{
            colour += vec3(skycol);
        }
    }
    colour /= 1.0;
    vec4 data = texture(iChannel0,th);
    data += vec4(colour,1);
    fragColor = data;
}
