vec2 rotate(float offset)
{
    vec2 pos = vec2(sin(iTime/2.0+offset), cos(iTime/2.0+offset));
    return pos;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy - 0.5) * 40.0;
    uv.x *= iResolution.x/iResolution.y;
    //uv = asinh(uv);

    // Time varying pixel color
    float r = sin(length(uv+(rotate(0.0))*abs(sin(iTime/2.0))*2.0)-iTime*4.0);
    float g = sin(length(uv+(rotate(2.0))*abs(sin(iTime/2.0))*2.0)-iTime*4.0);
    float b = sin(length(uv+(rotate(4.0))*abs(sin(iTime/2.0))*2.0)-iTime*4.0);

    // Output to screen
    fragColor = vec4(r*r/g, g*g/b, b*b/r,1.0);
}
