float lerp(float a, float b, float t)
{
    return a + (b - a) * t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 fire_color = vec3(1.0, 0.1, 0.1);
    vec2 scroll_direction = vec2(0.0, -1.0);
    float scroll_speed = 0.5;

    //Step 1: Scroll up
    vec2 uv = fragCoord/iResolution.xy;
    vec2 scrolling = scroll_direction * scroll_speed * iTime;
    vec2 waving = vec2(0.01 * sin(5.0 * iTime), 0.0); 
    vec3 noise = texture(iChannel0, uv + waving + scrolling).xxx;

    //Step 2: Dark at the top, light at the bottom
    vec3 color = vec3(lerp(3.0, -1.0, uv.y));

    //Step 3: Combine!
    fragColor = vec4(fire_color * noise * color, 1.0);
}
