void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec3 col = vec3(abs(uv.x * uv.y) * pow(2.0, iTime));

    col = vec3(mod(col.x, 16777216.0) - mod(col.x, 65536.0), mod(col.x, 65536.0) - mod(col.x, 256.0), mod(col.x, 256.0));
    fragColor = vec4(col, 1.0);
}
