void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = fragCoord.xy / iResolution.xy;


    vec2 center = vec2(0.5, 0.5);

    vec2 size = vec2(0.1, 0.1);

    vec2 velocity = vec2(0.3, 0.2);


    vec2 newPosition = center + velocity * iTime;


    newPosition = newPosition * vec2(800.0 / 450.0, 1.0);
    newPosition = mod(newPosition, vec2(1.0, 1.0));

    if (uv.x > newPosition.x - size.x / 2.0 && uv.x < newPosition.x + size.x / 2.0 &&
        uv.y > newPosition.y - size.y / 2.0 && uv.y < newPosition.y + size.y / 2.0) {
        fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    } else {
        fragColor = vec4(1.0, 1.0, 1.0, 1.0); 
    }
}
