float quad (vec2 st, vec2 pivotPoint, float resizeY, float resizeX, float borderPct)
{
    vec2 stPivoted = st + pivotPoint;
  	vec2 borders =  step(vec2(borderPct),vec2(stPivoted.x + resizeX,stPivoted.y + resizeY)) *
          step(vec2(borderPct),1.0-vec2(stPivoted.x - resizeX,stPivoted.y - resizeY));

    return borders.x * borders.y;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    vec3 color = vec3(0.0);

    // bottom-left
    vec2 bl = step(vec2(0.1),st);
    float pct = bl.x * bl.y;

    // top-right
    vec2 tr = step(vec2(0.1),1.0-st);
    pct *= tr.x * tr.y;

    float rectangle = quad(st, vec2(0.490,-0.480), -0.328, -0.384, 0.05);
    float rectangle02 = quad(st, vec2(0.490-.155,-0.480), -0.328, -0.384, 0.05);
    float rectangle03 = quad(st, vec2(0.490,-0.480+.265), -0.328, -0.384, 0.05);
    float rectangle04 = quad(st, vec2(0.490-.155,-0.480+.265), -0.328, -0.384, 0.05);
    float rectangle05 = quad(st, vec2(0.390,0.140+.095), -0.144, -0.328, 0.05);
    float rectangle06 = quad(st, vec2(0.019,0.140+0.031), -0.208, -0.224, 0.05);
    float rectangle07 = quad(st, vec2(0.019,0.140+0.535)     , -0.208, -0.224, 0.05);
    float rectangle08 = quad(st, vec2(0.019,-0.480), -0.328, -0.224, 0.05);
    float rectangle09 = quad(st, vec2(0.019+-0.344,-0.480+.265)     , -0.328, -0.352, 0.05);
    float rectangle10 = quad(st, vec2(0.019,-0.480+.265)     , -0.328, -0.224, 0.05);
    float rectangle11 = quad(st, vec2(0.019+-0.344,-0.480)     , -0.328, -0.352, 0.05);
    float rectangle12 = quad(st, vec2(0.019+-0.344,0.140+0.031), -0.208, -0.352, 0.05);
    float rectangle13 = quad(st, vec2(0.019+-0.344,0.140+0.535), -0.208, -0.352, 0.05);
    float rectangle14 = quad(st, vec2(0.019+-0.562,-0.480+.265)     , -0.328, -0.352, 0.05);
    float rectangle15 = quad(st, vec2(0.019+-0.562,-0.480)     , -0.328, -0.352, 0.05);
    float rectangle16 = quad(st, vec2(0.019+-0.562,0.140+0.031), -0.208, -0.352, 0.05);
    float rectangle17 = quad(st, vec2(0.019+-0.562,0.140+0.535), -0.208, -0.352, 0.05);

    vec3 colorRed = vec3(0.405,0.124,0.077);
    vec3 colorWhite = vec3(0.810,0.796,0.689);
    vec3 colorYellow = vec3(0.790,0.705,0.365);
    vec3 colorBlue = vec3(0.223,0.347,0.605);

    color = vec3(rectangle * colorRed + 
                rectangle02 * colorRed+ 
                rectangle03 * colorRed+ 
                rectangle04 * colorRed+ 
                rectangle05 * colorWhite+ 
                rectangle06 * colorWhite+ 
                rectangle07 * colorWhite+ 
                rectangle08 * colorWhite+ 
                rectangle09 * colorWhite+ 
                rectangle10 * colorWhite+ 
                rectangle11 * colorWhite+ 
                rectangle12 * colorWhite+ 
                rectangle13 * colorBlue+ 
                rectangle14 * colorYellow+ 
                rectangle15 * colorYellow+ 
                rectangle16 * colorWhite+ 
                rectangle17 * colorBlue
                );

    fragColor = vec4(color,1.0);
}
